var link=document.querySelector(".popup"),overlay=document.querySelector(".modal-overlay"),modal=document.querySelector(".modal"),close=modal.querySelector(".close"),login=modal.querySelector(".input-text"),email=modal.querySelector(".input-email"),text=modal.querySelector(".textarea");link.addEventListener("click",function(e){e.preventDefault(),overlay.classList.add("visible"),modal.classList.add("visible"),modal.classList.add("dropping"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),overlay.classList.remove("visible"),modal.classList.remove("visible"),modal.classList.remove("modal-error")}),modal.addEventListener("submit",function(e){login.value&&email.value&&text.value||(e.preventDefault(),modal.classList.remove("modal-error"),modal.offsetWidth=modal.offsetWidth,modal.classList.add("modal-error"))});
